
const toolPen = {
  name: "Pen",
  icon: "pencil icon",        // Semantic-UI icon CSS class
  editCursor: "crosshair",
  supportsDrag: true,

  handleToolSelected: ( drawEnv ) => {},
  handleToolDeselected: ( drawEnv ) => {},

  handleMouseDown: ( drawEnv ) => {
    // x,y are the unscaled pixel coordinates of the mousedown
    // drawEnv is in the format generated by EditGraphic.collateDrawingToolEnv()

    // Change Preview canvas (which is the real data)
    drawEnv.previewCtx.fillStyle = drawEnv.chosenColor.hex
    drawEnv.previewCtx.fillRect(drawEnv.x, drawEnv.y, 1, 1)

    // Change Edit canvas (which is derived from the preview)
    drawEnv.editCtx.fillStyle = drawEnv.chosenColor.hex
    drawEnv.editCtx.fillRect( drawEnv.x * drawEnv.scale,
                              drawEnv.y * drawEnv.scale,
                              drawEnv.scale,
                              drawEnv.scale)
  },

  handleMouseMove: ( drawEnv ) => { toolPen.handleMouseDown(drawEnv) },

  handleMouseUp: ( drawEnv ) => {},

  handleMouseLeave: ( drawEnv ) => {}

};

export default toolPen
