const ToolRedo = {
  label: "Redo",
  name: "redoTool",
  iconText: 2,
  tooltip: "Redo",
  icon: "undo flip icon",        // Semantic-UI icon CSS class
  editCursor: "crosshair",
  supportsDrag: false,
  shortcut: 'Ctrl+Shift+Z',
  changesImage: false,             // This does cause changes to the image, so image is dirty if this tool used
  level: 2,

  handleMouseDown: ( drawEnv ) => {
    // drawEnv is in the format generated by EditGraphic.collateDrawingToolEnv()

    ToolRedo._storedPreviewImageData = drawEnv.previewCtx.getImageData(0,0, drawEnv.width, drawEnv.height)
    ToolRedo._startx = drawEnv.x
    ToolRedo._starty = drawEnv.y
    drawEnv.unselect();
  },

  handleMouseMove: ( drawEnv ) => {

    let w = drawEnv.x - ToolRedo._startx
    let h = drawEnv.y - ToolRedo._starty

    drawEnv.saveSelectRect(ToolRedo._startx, ToolRedo._starty, drawEnv.x, drawEnv.y);

    // update canvas clearing from previous select area
    drawEnv.updateEditCanvasFromSelectedPreviewCanvas()
  },

  handleMouseUp: ( drawEnv ) => {
    ToolRedo._storedPreviewImageData = null
    ToolRedo._startx = null
    ToolRedo._starty = null

  },

  handleMouseLeave: ( drawEnv ) => {
    ToolRedo.handleMouseUp()
  }

}

export default ToolRedo